"""tests/test_config.pyPurpose-------Sanity checks for build_config defaults (types/ranges).No network geocoding is triggered here because we don't call square_bbox_around."""from spongekit_core.config import build_configdef test_build_config_defaults():    cfg = build_config(place="Amsterdam, Netherlands", tile_km=1.0, storm_mm=50.0)    assert isinstance(cfg.storm_mm, float)    assert cfg.C_roof == 0.9    assert isinstance(cfg.crs_projected, int)    assert cfg.tile_km > 0