# tests/test_hydro.py"""tests/test_hydro.pyPurpose-------Check core arithmetic for baseline runoff and a green roof scenario usingthe exact parameter order from spongekit_core.hydro.All units:- P_mm in millimetres- Areas in m²- Volumes in m³"""from spongekit_core.hydro import baseline_runoff, scenario_runoff_green_roofsdef test_baseline_runoff_simple():    """    P = 50 mm, C_roof = 0.9, A = 100 m²    Volume = (50 mm) * 0.9 * (100 m²) / 1000 = 4.5 m³    """    P_mm = 50    C_roof = 0.9    A_total = 100    V = baseline_runoff(P_mm, C_roof, A_total)    assert abs(V - 4.5) < 1e-9def test_scenario_vs_baseline_consistency():    """    Scenario runoff should be <= baseline when some area is greened.    Align call order with hydro.scenario_runoff_green_roofs signature:      mode, P_mm, R_mm, C_roof, Cg, A_total_m2, A_green_m2, hyeto    """    P_mm = 40.0    C_roof = 0.9    Cg = 0.25    R_mm = 15.0    A_total = 1000.0    A_green = 300.0  # 30% converted to green roof    V_base = baseline_runoff(P_mm, C_roof, A_total)    V_scn, retained = scenario_runoff_green_roofs(        "depth",   # mode (first)        P_mm,      # rainfall depth        R_mm,      # green roof storage        C_roof,    # baseline roof runoff coefficient        Cg,        # green roof overflow coefficient        A_total,   # total area (m²)        A_green,   # greened area (m²)        None,      # hyetograph (not used in depth mode)    )    assert V_scn <= V_base + 1e-9    assert retained >= 0    assert abs(retained - (V_base - V_scn)) < 1e-9