"""tests/test_hydro.pyPurpose-------Check core arithmetic for baseline runoff and a green roof scenario.All units:- P_mm in millimetres- Areas in m²- Volumes in m³"""from spongekit_core.hydro import baseline_runoff, scenario_runoff_green_roofsdef test_baseline_runoff_simple():    """    P = 50 mm, C_roof = 0.9, A = 100 m²    Volume = (50 mm) * 0.9 * (100 m²) / 1000 = 4.5 m³    """    V = baseline_runoff(P_mm=50, C_roof=0.9, A_total=100)    assert abs(V - 4.5) < 1e-9def test_scenario_vs_baseline_consistency():    """    Simple check: scenario runoff should be <= baseline runoff when some area is greened.    """    P_mm = 40.0    C_roof = 0.9    Cg = 0.25    R_mm = 15.0    A_total = 1000.0    A_green = 300.0  # 30% converted to green roof    V_base = baseline_runoff(P_mm=P_mm, C_roof=C_roof, A_total=A_total)    V_scn, retained = scenario_runoff_green_roofs(        P_mm=P_mm,        R_mm=R_mm,        C_roof=C_roof,        Cg=Cg,        A_total=A_total,        A_green=A_green,        mode="depth",        hyeto=None,    )    assert V_scn <= V_base + 1e-9    assert retained >= 0    # Retained should match the reduction relative to baseline    assert abs(retained - (V_base - V_scn)) < 1e-9